<?php 
	function cards_script(){
?>

	<script type="text/javascript">
	jQuery(document).ready(function(){
		jQuery('.paca-standard-card').each(function(){
			var height = jQuery(this).find('.paca-card-contents').innerHeight();
			jQuery(this).find('.paca-card-contents').attr('data-height',height);
			jQuery(this).find('.paca-card-contents').css('height','0');
		});

		jQuery('.paca-card-button-inner').click(function(){


			var height = jQuery(this).parents('.paca-standard-card').find('.paca-card-contents').attr('data-height');
			var speed  = jQuery(this).parents('.paca-standard-card').find('.paca-card-contents').attr('data-speed');
			
			jQuery(this).toggleClass('active');
			jQuery(this).parents('.paca-standard-card').find('.paca-card-contents').toggleClass('active');
			jQuery(this).parents('.paca-standard-card').find('.paca-learn-more-btn-x').toggleClass('active');

			if(jQuery(this).hasClass('active')){
				jQuery(this).parents('.paca-standard-card').find('.paca-card-contents').animate({
					'height':height,
				},speed);				
			}else{
				jQuery(this).parents('.paca-standard-card').find('.paca-card-contents').animate({
					'height':'0',
				},speed);					
			}

		});

	});



	</script>



<?php
	}

	add_action('wp_footer','cards_script',999);
?>



